import { Component } from '@angular/core';
import { LoremIpsum } from 'lorem-ipsum';

@Component({
  selector: 'app-structural-directives',
  templateUrl: './structural-directives.component.html',
  styleUrls: ['./structural-directives.component.css']
})
export class StructuralDirectivesComponent {
  paragraph = new LoremIpsum().generateParagraphs(1);
  paragraphVisible = true;

  toggleParagraph() {
    this.paragraphVisible = !this.paragraphVisible;
  }

}
<div class="border border-2 border-secondary">
    <div class="bg-info text-center">template-variables.component</div>

    <div class="row mt-2 mb-2 p-2">
        <div class= "col">
            <button class="btn btn-primary" (click)="toggleParagraph()">
                {{ paragraphVisible? "Hide text" : "Reveal text"}}
            </button>
        </div>
        <div *ngIf="paragraphVisible" class="col">{{ paragraph }} </div>
    </div>
</div>
