import { Component, ViewChild } from '@angular/core';
import { User } from '../app.interfaces';
import { NgForm } from '@angular/forms';

@Component({
  selector: 'app-template-forms',
  templateUrl: './template-forms.component.html',
  styleUrls: ['./template-forms.component.css']
})
export class TemplateFormsComponent {
  @ViewChild(NgForm) form!: NgForm;
users: User[] = [];

  onSubmit(formData: any) {
    const formValue = formData.value as User;
    this.users.push(formValue);
    this.form.reset();
  }
}
<div class="row">
<div class="col">
    <form #form="ngForm" (ngSubmit)="onSubmit(form)">
    <p>
        <label for="firstname" class="form-label">First Name</label>
        <input type="text" name="firstname" class="form-control" ngModel>
    </p>
    <p>
        <label for="lastname" class="form-label">Last Name</label>
        <input type="text" name="lastname" class="form-control" ngModel>
    </p>
    <p>
        <label for="age" class="form-label">Age</label>
        <input type="number" name="age" class="form-control" ngModel>
    </p>
    <p>
        <button type="submit" class="btn btn-primary">Submit</button>
    </p>
</form>
</div>
<div class="col">
    <app-simple-table0 [userData]="users"></app-simple-table0>
</div>
</div>
