import { Component, EventEmitter, Input, Output } from '@angular/core';
import { User } from '../app.interfaces';

@Component({
  selector: 'app-simple-table1',
  templateUrl: './simple-table1.component.html',
  styleUrls: ['./simple-table1.component.css']
})
export class SimpleTable1Component {
   @Input() userData: User[] = [];
   @Output() deleteUser = new EventEmitter<number>();
   @Output() sendUser = new EventEmitter<User>();

   onDeleteButtonClick(i: number) {
    this.deleteUser.emit(i);
   }

   onSendUserButtonClick(user: User) {
    this.sendUser.emit(user)
   }
}
<table class="table">
    <thead class="table-info">
        <tr>
            <td>#</td>
            <td>First Name</td>
            <td>Last Name</td>
            <td>Age</td>
            <td></td>
            <td></td>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let user of userData; index as i">
            <td> {{ i }} </td>
            <td>{{ user.firstname }} </td>
            <td>{{ user.lastname }} </td>
            <td>{{ user.age }} </td>
            <td>
                <button (click)= "onDeleteButtonClick(i)" class="btn btn-danger btn-sm">
                    Delete
                </button>
            </td>
            <td>
                <button (click)="onSendUserButtonClick(user)"
                 class="btn btn-success btn-sm">
                    Send user
                </button>
            </td>
        </tr>
    </tbody>
</table>
