import { Component } from '@angular/core';

@Component({
  selector: 'app-my-hello-component',
  templateUrl: './my-hello-component.component.html',
  styleUrls: ['./my-hello-component.component.css']
})
export class MyHelloComponentComponent {
  imageURL = "https://picsum.photos/200";
  imageALT = "An image from picsum photos";
  imageTooltip = this.imageALT;

  buttonDisabled = false;
  buttonPrompt = 'Click me'

  times = 0;
  userInput = '';

  onButtonClick() {
    this.times += 1;
    console.log(this.times);
  }

  resetCounter() {
    this.times = 0;
  }

  onUserInput(event: Event) {
    const target = event.target as HTMLInputElement;
    this.userInput = target.value;
  }
}
<div class = "border border-2 border-danger p-2">
    <div class = "row">
        <div class = "col">
            <img 
            src = "{{ imageURL }}" 
            alt = "{{ imageALT}}" 
            title = "{{imageTooltip}}" 
            >
            <button disabled = "{{ buttonDisabled? 'disabled': null}}" 
            class = "btn btn-primary">
                {{buttonPrompt}}
            </button>

        </div>
        <div class = "col">
            <img 
            [src] = "imageURL" 
            [alt] = "imageALT" 
            [title] = "imageTooltip" />
            <button [disabled]="buttonDisabled" class = "btn btn-primary">
                {{buttonPrompt}}
            </button>
        </div>
    </div>
    <div class="row mt-2">
        <div class="col">
            <button (click) = "onButtonClick()" class="btn btn-primary">
                Count Clicks
            </button>
            <button (click)="resetCounter()" class="ms-2 btn btn-success">Reset</button>
        </div>
        <div class="col">
            Button clicked {{times}} times
        </div>
    </div>
    <div class="row mt-2">
        <div class="col">
            <input (input)="onUserInput($event)" class="form-control">
        </div>
        <div class="col">
            {{userInput}}
        </div>
    </div>
</div>
