import { Component } from '@angular/core';
import { LoremIpsum } from 'lorem-ipsum';

interface User {
  firstname: string;
  lastname: string;
  age: number;
}

@Component({
  selector: 'app-structural-directives',
  templateUrl: './structural-directives.component.html',
  styleUrls: ['./structural-directives.component.css']
})
export class StructuralDirectivesComponent {
  paragraph = new LoremIpsum().generateParagraphs(1);
  paragraphVisible = true;

  userData: User[] = [
    {firstname: 'John', lastname: 'Doe', age:20},
    {firstname: 'Jane', lastname: 'Smith', age:19},
    {firstname: 'Joe', lastname: 'Scmoe', age:33},
    {firstname: 'Jane', lastname: 'Brown', age:34},

  ]

  toggleParagraph() {
    this.paragraphVisible = !this.paragraphVisible;
  }

}
<div class="border border-2 border-secondary">
    <div class="bg-info text-center">structural-directives.component</div>

    <div class="row mt-2 mb-2 p-2">
        <div class= "col">
            <button class="btn btn-primary" (click)="toggleParagraph()">
                {{ paragraphVisible? "Hide text" : "Reveal text"}}
            </button>
        </div>
        <div *ngIf="paragraphVisible" class="col">{{ paragraph }} </div>
    </div>

    <div class= "p-2">
        <table class="table">
            <thead class="table-info">
                <tr>
                    <td>#</td>
                    <td>First Name</td>
                    <td>Last Name</td>
                    <td>Age</td>
                </tr>
            </thead>
            <tbody>
                <tr *ngFor="let user of userData; index as i">
                    <td> {{ i }} </td>
                    <td>{{ user.firstname }} </td>
                    <td>{{ user.lastname }} </td>
                    <td>{{ user.age }} </td>
                </tr>
            </tbody>
        </table>

    </div>
</div>
